contracts/ForecastData.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title ForecastData
 * @dev Smart contract for storing and managing AI forecast data on-chain
 * Features:
 * - Immutable forecast storage with timestamps
 * - Accuracy tracking and verification
 * - Data integrity and audit trails
 * - Incentivized accurate predictions
 */
contract ForecastData is Ownable, Pausable {
    struct Forecast {
        address forecaster;
        string storeId;
        string city;
        uint256 timestamp;
        uint256 predictionPeriodStart;
        uint256 predictionPeriodEnd;
        string dataHash; // IPFS hash or data hash for verification
        uint256[] predictedSales; // Array of predicted sales values
        uint256[] actualSales; // Array of actual sales values (filled later)
        uint256 accuracy; // Calculated accuracy percentage * 100
        bool isVerified;
        bool accuracyCalculated;
    }

    struct Store {
        string name;
        string location;
        address owner;
        bool isActive;
        uint256 totalForecasts;
        uint256 averageAccuracy;
    }

    // State variables
    mapping(uint256 => Forecast) public forecasts;
    mapping(string => Store) public stores;
    mapping(address => uint256[]) public userForecasts;
    mapping(string => uint256[]) public storeForecasts;

    uint256 public forecastCounter;
    uint256 public constant MIN_ACCURACY_FOR_REWARD = 8000; // 80%

    // Authorized data providers (can submit actual sales data)
    mapping(address => bool) public dataProviders;

    // Events
    event ForecastSubmitted(
        uint256 indexed forecastId,
        address indexed forecaster,
        string storeId,
        uint256 predictionPeriodStart,
        uint256 predictionPeriodEnd
    );
    event ActualDataSubmitted(
        uint256 indexed forecastId,
        address indexed dataProvider
    );
    event AccuracyCalculated(uint256 indexed forecastId, uint256 accuracy);
    event StoreRegistered(string indexed storeId, string name, address owner);
    event DataProviderAdded(address indexed provider);
    event DataProviderRemoved(address indexed provider);

    constructor(address initialOwner) Ownable(initialOwner) {}

    /**
     * @dev Register a new store
     */
    function registerStore(
        string memory storeId,
        string memory name,
        string memory location,
        address storeOwner
    ) external onlyOwner {
        require(bytes(storeId).length > 0, "Store ID cannot be empty");
        require(
            stores[storeId].owner == address(0),
            "Store already registered"
        );

        stores[storeId] = Store({
            name: name,
            location: location,
            owner: storeOwner,
            isActive: true,
            totalForecasts: 0,
            averageAccuracy: 0
        });

        emit StoreRegistered(storeId, name, storeOwner);
    }

    /**
     * @dev Submit a new forecast
     */
    function submitForecast(
        string memory storeId,
        string memory city,
        uint256 predictionPeriodStart,
        uint256 predictionPeriodEnd,
        string memory dataHash,
        uint256[] memory predictedSales
    ) external whenNotPaused returns (uint256) {
        require(bytes(storeId).length > 0, "Store ID cannot be empty");
        require(stores[storeId].isActive, "Store is not active");
        require(
            predictionPeriodStart < predictionPeriodEnd,
            "Invalid prediction period"
        );
        require(predictedSales.length > 0, "Predicted sales cannot be empty");
        require(
            predictionPeriodStart > block.timestamp,
            "Prediction period must be in the future"
        );

        forecastCounter++;
        uint256 forecastId = forecastCounter;

        forecasts[forecastId] = Forecast({
            forecaster: msg.sender,
            storeId: storeId,
            city: city,
            timestamp: block.timestamp,
            predictionPeriodStart: predictionPeriodStart,
            predictionPeriodEnd: predictionPeriodEnd,
            dataHash: dataHash,
            predictedSales: predictedSales,
            actualSales: new uint256[](0),
            accuracy: 0,
            isVerified: false,
            accuracyCalculated: false
        });

        userForecasts[msg.sender].push(forecastId);
        storeForecasts[storeId].push(forecastId);
        stores[storeId].totalForecasts++;

        emit ForecastSubmitted(
            forecastId,
            msg.sender,
            storeId,
            predictionPeriodStart,
            predictionPeriodEnd
        );

        return forecastId;
    }

    /**
     * @dev Submit actual sales data (only data providers)
     */
    function submitActualData(
        uint256 forecastId,
        uint256[] memory actualSales
    ) external {
        require(
            dataProviders[msg.sender] || msg.sender == owner(),
            "Not authorized to submit actual data"
        );

        Forecast storage forecast = forecasts[forecastId];
        require(forecast.forecaster != address(0), "Forecast does not exist");
        require(!forecast.accuracyCalculated, "Accuracy already calculated");
        require(
            block.timestamp > forecast.predictionPeriodEnd,
            "Prediction period not ended"
        );
        require(
            actualSales.length == forecast.predictedSales.length,
            "Sales data length mismatch"
        );

        forecast.actualSales = actualSales;
        forecast.isVerified = true;

        // Calculate accuracy
        uint256 accuracy = _calculateAccuracy(
            forecast.predictedSales,
            actualSales
        );
        forecast.accuracy = accuracy;
        forecast.accuracyCalculated = true;

        // Update store average accuracy
        _updateStoreAccuracy(forecast.storeId);

        emit ActualDataSubmitted(forecastId, msg.sender);
        emit AccuracyCalculated(forecastId, accuracy);
    }

    /**
     * @dev Calculate forecast accuracy using Mean Absolute Percentage Error (MAPE)
     */
    function _calculateAccuracy(
        uint256[] memory predicted,
        uint256[] memory actual
    ) internal pure returns (uint256) {
        require(predicted.length == actual.length, "Array length mismatch");

        uint256 totalError = 0;
        uint256 validPairs = 0;

        for (uint256 i = 0; i < predicted.length; i++) {
            if (actual[i] > 0) {
                // Avoid division by zero
                uint256 error = actual[i] > predicted[i]
                    ? actual[i] - predicted[i]
                    : predicted[i] - actual[i];
                totalError += (error * 10000) / actual[i]; // Multiply by 10000 for precision
                validPairs++;
            }
        }

        if (validPairs == 0) return 0;

        uint256 mape = totalError / validPairs;
        uint256 accuracy = mape > 10000 ? 0 : 10000 - mape; // Convert MAPE to accuracy percentage

        return accuracy;
    }

    /**
     * @dev Update store's average accuracy
     */
    function _updateStoreAccuracy(string memory storeId) internal {
        uint256[] memory storeForcastIds = storeForecasts[storeId];
        uint256 totalAccuracy = 0;
        uint256 verifiedForecasts = 0;

        for (uint256 i = 0; i < storeForcastIds.length; i++) {
            Forecast storage forecast = forecasts[storeForcastIds[i]];
            if (forecast.accuracyCalculated) {
                totalAccuracy += forecast.accuracy;
                verifiedForecasts++;
            }
        }

        if (verifiedForecasts > 0) {
            stores[storeId].averageAccuracy = totalAccuracy / verifiedForecasts;
        }
    }

    /**
     * @dev Get forecast details
     */
    function getForecast(
        uint256 forecastId
    )
        external
        view
        returns (
            address forecaster,
            string memory storeId,
            string memory city,
            uint256 timestamp,
            uint256 predictionPeriodStart,
            uint256 predictionPeriodEnd,
            string memory dataHash,
            uint256[] memory predictedSales,
            uint256[] memory actualSales,
            uint256 accuracy,
            bool isVerified,
            bool accuracyCalculated
        )
    {
        Forecast memory forecast = forecasts[forecastId];
        return (
            forecast.forecaster,
            forecast.storeId,
            forecast.city,
            forecast.timestamp,
            forecast.predictionPeriodStart,
            forecast.predictionPeriodEnd,
            forecast.dataHash,
            forecast.predictedSales,
            forecast.actualSales,
            forecast.accuracy,
            forecast.isVerified,
            forecast.accuracyCalculated
        );
    }

    /**
     * @dev Get user's forecasts
     */
    function getUserForecasts(
        address user
    ) external view returns (uint256[] memory) {
        return userForecasts[user];
    }

    /**
     * @dev Get store's forecasts
     */
    function getStoreForecasts(
        string memory storeId
    ) external view returns (uint256[] memory) {
        return storeForecasts[storeId];
    }

    /**
     * @dev Get store information
     */
    function getStore(
        string memory storeId
    )
        external
        view
        returns (
            string memory name,
            string memory location,
            address owner,
            bool isActive,
            uint256 totalForecasts,
            uint256 averageAccuracy
        )
    {
        Store memory store = stores[storeId];
        return (
            store.name,
            store.location,
            store.owner,
            store.isActive,
            store.totalForecasts,
            store.averageAccuracy
        );
    }

    /**
     * @dev Check if forecast is eligible for reward
     */
    function isEligibleForReward(
        uint256 forecastId
    ) external view returns (bool) {
        Forecast memory forecast = forecasts[forecastId];
        return
            forecast.accuracyCalculated &&
            forecast.accuracy >= MIN_ACCURACY_FOR_REWARD;
    }

    /**
     * @dev Add data provider
     */
    function addDataProvider(address provider) external onlyOwner {
        dataProviders[provider] = true;
        emit DataProviderAdded(provider);
    }

    /**
     * @dev Remove data provider
     */
    function removeDataProvider(address provider) external onlyOwner {
        dataProviders[provider] = false;
        emit DataProviderRemoved(provider);
    }

    /**
     * @dev Deactivate a store
     */
    function deactivateStore(string memory storeId) external onlyOwner {
        stores[storeId].isActive = false;
    }

    /**
     * @dev Activate a store
     */
    function activateStore(string memory storeId) external onlyOwner {
        stores[storeId].isActive = true;
    }

    /**
     * @dev Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/PlatformControlledToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

/**
 * @title PlatformControlledToken
 * @dev Business tokenization platform where:
 * 1. Platform owner controls entire token supply
 * 2. Retailers must buy tokens from platform to tokenize businesses
 * 3. Platform earns fees on all transactions
 * 4. Controlled distribution model for business tokenization
 */
contract PlatformControlledToken is ERC20, Ownable, ReentrancyGuard {
    using Math for uint256;

    // ================== Constants ==================
    uint256 public constant TOTAL_SUPPLY = 1_000_000_000 * 10 ** 18; // 1 billion tokens
    uint256 public constant PLATFORM_FEE_PERCENT = 2; // 2% platform fee
    uint256 public constant MIN_PURCHASE_AMOUNT = 1000 * 10 ** 18; // Minimum 1000 tokens

    // ================== State Variables ==================
    address public platformFeeCollector;
    uint256 public platformTokenPrice = 1 ether; // 1 ETH per 1000 tokens initially
    uint256 public totalPlatformRevenue;

    mapping(address => bool) public authorizedRetailers;
    mapping(address => uint256) public retailerPurchaseHistory;
    mapping(address => bool) public exemptFromFees; // Platform wallets exempt from fees

    // ================== Events ==================
    event RetailerAuthorized(address indexed retailer, uint256 timestamp);
    event RetailerRevoked(address indexed retailer, uint256 timestamp);
    event TokensPurchasedFromPlatform(
        address indexed buyer,
        uint256 ethAmount,
        uint256 tokensReceived,
        uint256 timestamp
    );
    event PlatformFeeCollected(
        address indexed from,
        address indexed to,
        uint256 feeAmount,
        uint256 timestamp
    );
    event PriceUpdated(uint256 oldPrice, uint256 newPrice, uint256 timestamp);

    // ================== Custom Errors ==================
    error InsufficientPayment();
    error NotAuthorizedRetailer();
    error BelowMinimumPurchase();
    error NoTokensAvailable();
    error InvalidPrice();
    error TransferFailed();

    constructor(
        address _platformFeeCollector
    ) ERC20("ProfitHive Token", "PHIVE") Ownable(msg.sender) {
        platformFeeCollector = _platformFeeCollector;

        // Platform owner gets all tokens initially
        _mint(msg.sender, TOTAL_SUPPLY);

        // Platform wallets are exempt from fees
        exemptFromFees[msg.sender] = true;
        exemptFromFees[_platformFeeCollector] = true;
    }

    // ================== Platform Owner Functions ==================

    /**
     * @dev Set token price for retailers to purchase from platform
     * @param _newPrice New price in ETH per 1000 tokens
     */
    function setPlatformTokenPrice(uint256 _newPrice) external onlyOwner {
        if (_newPrice == 0) revert InvalidPrice();

        uint256 oldPrice = platformTokenPrice;
        platformTokenPrice = _newPrice;

        emit PriceUpdated(oldPrice, _newPrice, block.timestamp);
    }

    /**
     * @dev Authorize retailer to participate in business tokenization
     * @param _retailer Address of retailer to authorize
     */
    function authorizeRetailer(address _retailer) external onlyOwner {
        authorizedRetailers[_retailer] = true;
        emit RetailerAuthorized(_retailer, block.timestamp);
    }

    /**
     * @dev Revoke retailer authorization
     * @param _retailer Address of retailer to revoke
     */
    function revokeRetailer(address _retailer) external onlyOwner {
        authorizedRetailers[_retailer] = false;
        emit RetailerRevoked(_retailer, block.timestamp);
    }

    /**
     * @dev Set fee exemption status for platform wallets
     * @param _address Address to set exemption for
     * @param _exempt Whether to exempt from fees
     */
    function setFeeExemption(
        address _address,
        bool _exempt
    ) external onlyOwner {
        exemptFromFees[_address] = _exempt;
    }

    /**
     * @dev Withdraw platform revenue (ETH from token sales)
     * @param _amount Amount to withdraw
     */
    function withdrawPlatformRevenue(
        uint256 _amount
    ) external onlyOwner nonReentrant {
        if (_amount > address(this).balance) revert InsufficientPayment();

        (bool success, ) = payable(owner()).call{value: _amount}("");
        if (!success) revert TransferFailed();
    }

    // ================== Public Purchase Functions ==================

    /**
     * @dev Retailers and investors purchase tokens from platform
     * Platform controls all token distribution
     */
    function purchaseTokensFromPlatform() external payable nonReentrant {
        if (msg.value == 0) revert InsufficientPayment();

        // Calculate tokens to receive (price is per 1000 tokens)
        uint256 tokensToReceive = (msg.value * 1000 * 10 ** 18) /
            platformTokenPrice;

        if (tokensToReceive < MIN_PURCHASE_AMOUNT)
            revert BelowMinimumPurchase();
        if (balanceOf(owner()) < tokensToReceive) revert NoTokensAvailable();

        // Track retailer purchase history
        retailerPurchaseHistory[msg.sender] += tokensToReceive;
        totalPlatformRevenue += msg.value;

        // Transfer tokens from platform owner to buyer
        _transfer(owner(), msg.sender, tokensToReceive);

        emit TokensPurchasedFromPlatform(
            msg.sender,
            msg.value,
            tokensToReceive,
            block.timestamp
        );
    }

    // ================== Enhanced Transfer with Fees ==================

    /**
     * @dev Apply platform fees using _update hook (OpenZeppelin 5.x)
     * Platform earns fees on every transaction
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override {
        // Skip fees for platform wallets and minting/burning
        if (
            from == address(0) ||
            to == address(0) ||
            exemptFromFees[from] ||
            exemptFromFees[to]
        ) {
            super._update(from, to, amount);
            return;
        }

        // Calculate platform fee (2% of transfer amount)
        uint256 feeAmount = (amount * PLATFORM_FEE_PERCENT) / 100;
        uint256 transferAmount = amount - feeAmount;

        // Apply fee to fee collector first
        if (feeAmount > 0) {
            super._update(from, platformFeeCollector, feeAmount);
            emit PlatformFeeCollected(
                from,
                platformFeeCollector,
                feeAmount,
                block.timestamp
            );
        }

        // Then transfer remaining amount to recipient
        super._update(from, to, transferAmount);
    }

    // ================== View Functions ==================

    /**
     * @dev Get current token price for platform purchases
     * @return Price in ETH per 1000 tokens
     */
    function getCurrentTokenPrice() external view returns (uint256) {
        return platformTokenPrice;
    }

    /**
     * @dev Calculate how many tokens can be purchased with given ETH amount
     * @param _ethAmount Amount of ETH
     * @return Number of tokens that can be purchased
     */
    function calculateTokensForEth(
        uint256 _ethAmount
    ) external view returns (uint256) {
        return (_ethAmount * 1000 * 10 ** 18) / platformTokenPrice;
    }

    /**
     * @dev Calculate ETH needed to purchase specific amount of tokens
     * @param _tokenAmount Amount of tokens desired
     * @return ETH amount needed
     */
    function calculateEthForTokens(
        uint256 _tokenAmount
    ) external view returns (uint256) {
        return (_tokenAmount * platformTokenPrice) / (1000 * 10 ** 18);
    }

    /**
     * @dev Get platform statistics
     * @return totalSupply_ Total token supply
     * @return platformBalance Platform owner balance
     * @return totalRevenue Total platform revenue
     * @return currentPrice Current token price
     * @return contractBalance Contract ETH balance
     */
    function getPlatformStats()
        external
        view
        returns (
            uint256 totalSupply_,
            uint256 platformBalance,
            uint256 totalRevenue,
            uint256 currentPrice,
            uint256 contractBalance
        )
    {
        return (
            totalSupply(),
            balanceOf(owner()),
            totalPlatformRevenue,
            platformTokenPrice,
            address(this).balance
        );
    }

    /**
     * @dev Check if address is authorized retailer
     * @param _retailer Address to check
     * @return Whether retailer is authorized
     */
    function isAuthorizedRetailer(
        address _retailer
    ) external view returns (bool) {
        return authorizedRetailers[_retailer];
    }

    /**
     * @dev Get retailer purchase history
     * @param _retailer Retailer address
     * @return Total tokens purchased by retailer
     */
    function getRetailerPurchaseHistory(
        address _retailer
    ) external view returns (uint256) {
        return retailerPurchaseHistory[_retailer];
    }

    // ================== Emergency Functions ==================

    /**
     * @dev Emergency withdraw all ETH (owner only)
     */
    function emergencyWithdraw() external onlyOwner nonReentrant {
        uint256 balance = address(this).balance;
        (bool success, ) = payable(owner()).call{value: balance}("");
        if (!success) revert TransferFailed();
    }

    /**
     * @dev Update platform fee collector address
     * @param _newCollector New fee collector address
     */
    function updateFeeCollector(address _newCollector) external onlyOwner {
        platformFeeCollector = _newCollector;
        exemptFromFees[_newCollector] = true;
    }

    // ================== Receive ETH ==================
    receive() external payable {
        // Allow contract to receive ETH
    }
}

contracts/ProfitHiveToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";

/**
 * @title ProfitHiveToken
 * @dev ERC20 token for ProfitHive platform
 * Features:
 * - Revenue sharing rewards
 * - Staking for platform benefits
 * - Governance participation
 * - Forecast accuracy rewards
 */
contract ProfitHiveToken is ERC20, ERC20Burnable, Ownable, Pausable {
    uint256 public constant MAX_SUPPLY = 1_000_000_000 * 10 ** 18; // 1 billion tokens
    uint256 public constant INITIAL_SUPPLY = 100_000_000 * 10 ** 18; // 100 million initial

    // Roles
    mapping(address => bool) public minters;
    mapping(address => bool) public rewarders;

    // Events
    event MinterAdded(address indexed minter);
    event MinterRemoved(address indexed minter);
    event RewarderAdded(address indexed rewarder);
    event RewarderRemoved(address indexed rewarder);
    event RewardDistributed(
        address indexed recipient,
        uint256 amount,
        string reason
    );

    constructor(
        address initialOwner
    ) ERC20("ProfitHive Token", "PHIVE") Ownable(initialOwner) {
        _mint(initialOwner, INITIAL_SUPPLY);
    }

    /**
     * @dev Mint tokens - only callable by authorized minters
     */
    function mint(address to, uint256 amount) external {
        require(
            minters[msg.sender] || msg.sender == owner(),
            "Not authorized to mint"
        );
        require(totalSupply() + amount <= MAX_SUPPLY, "Exceeds max supply");
        _mint(to, amount);
    }

    /**
     * @dev Distribute rewards for forecast accuracy or platform participation
     */
    function distributeReward(
        address recipient,
        uint256 amount,
        string memory reason
    ) external {
        require(
            rewarders[msg.sender] || msg.sender == owner(),
            "Not authorized to reward"
        );
        require(totalSupply() + amount <= MAX_SUPPLY, "Exceeds max supply");

        _mint(recipient, amount);
        emit RewardDistributed(recipient, amount, reason);
    }

    /**
     * @dev Add authorized minter
     */
    function addMinter(address minter) external onlyOwner {
        minters[minter] = true;
        emit MinterAdded(minter);
    }

    /**
     * @dev Remove authorized minter
     */
    function removeMinter(address minter) external onlyOwner {
        minters[minter] = false;
        emit MinterRemoved(minter);
    }

    /**
     * @dev Add authorized rewarder
     */
    function addRewarder(address rewarder) external onlyOwner {
        rewarders[rewarder] = true;
        emit RewarderAdded(rewarder);
    }

    /**
     * @dev Remove authorized rewarder
     */
    function removeRewarder(address rewarder) external onlyOwner {
        rewarders[rewarder] = false;
        emit RewarderRemoved(rewarder);
    }

    /**
     * @dev Pause token transfers
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Override transfer functions to respect pause state
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._update(from, to, amount);
    }
}

contracts/ProfitHiveTokenV2.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ProfitHiveToken - Business Tokenomics Model
 * @dev ERC20 token with proper business model:
 * - Business owner controls token distribution
 * - Transaction fees generate platform revenue
 * - Retailers can purchase tokens to distribute to customers
 * - Staking rewards funded by platform fees
 * - Clear revenue streams for sustainable business
 */
contract ProfitHiveToken is
    ERC20,
    ERC20Burnable,
    Ownable,
    Pausable,
    ReentrancyGuard
{
    uint256 public constant MAX_SUPPLY = 1_000_000_000 * 10 ** 18; // 1 billion tokens
    uint256 public constant BUSINESS_RESERVE = 500_000_000 * 10 ** 18; // 50% for business owner
    uint256 public constant PUBLIC_SALE = 400_000_000 * 10 ** 18; // 40% for public sale
    uint256 public constant TEAM_RESERVE = 100_000_000 * 10 ** 18; // 10% for team/advisors

    // Business Model Parameters
    uint256 public transactionFeeRate = 100; // 1% (100 basis points)
    uint256 public constant MAX_FEE_RATE = 500; // 5% maximum fee
    uint256 public tokenPrice = 0.001 ether; // Price per token in ETH

    // Revenue Tracking
    uint256 public totalFeesCollected;
    uint256 public totalTokensSold;

    // Addresses
    address public feeCollector;
    address public treasuryWallet;

    // Roles and Permissions
    mapping(address => bool) public authorizedMinters;
    mapping(address => bool) public retailers;
    mapping(address => bool) public feeExempt; // Addresses exempt from transaction fees

    // Sales Tracking
    mapping(address => uint256) public purchaseHistory;
    mapping(address => uint256) public retailerSales;

    // Events
    event TokensPurchased(
        address indexed buyer,
        uint256 amount,
        uint256 ethPaid
    );
    event TokensSoldToRetailer(
        address indexed retailer,
        uint256 amount,
        uint256 ethPaid
    );
    event TransactionFeeCollected(
        address indexed from,
        address indexed to,
        uint256 feeAmount
    );
    event FeeRateUpdated(uint256 oldRate, uint256 newRate);
    event TokenPriceUpdated(uint256 oldPrice, uint256 newPrice);
    event RetailerAuthorized(address indexed retailer);
    event RetailerDeauthorized(address indexed retailer);

    error InsufficientPayment();
    error ExceedsMaxSupply();
    error InvalidFeeRate();
    error InvalidPrice();
    error NotAuthorizedRetailer();
    error TransferToZeroAddress();

    constructor(
        address initialOwner,
        address _treasuryWallet,
        address _feeCollector
    ) ERC20("ProfitHive Token", "PHIVE") Ownable(initialOwner) {
        require(_treasuryWallet != address(0), "Invalid treasury wallet");
        require(_feeCollector != address(0), "Invalid fee collector");

        treasuryWallet = _treasuryWallet;
        feeCollector = _feeCollector;

        // Mint business reserve to owner
        _mint(initialOwner, BUSINESS_RESERVE);

        // Set owner as fee exempt
        feeExempt[initialOwner] = true;
        feeExempt[address(this)] = true;
    }

    /**
     * @dev Public token purchase function
     * Anyone can buy tokens at current price
     */
    function purchaseTokens(uint256 tokenAmount) external payable nonReentrant {
        uint256 requiredEth = (tokenAmount * tokenPrice) / 10 ** 18;
        if (msg.value < requiredEth) revert InsufficientPayment();

        uint256 newTotalSupply = totalSupply() + tokenAmount;
        if (newTotalSupply > MAX_SUPPLY) revert ExceedsMaxSupply();

        // Mint tokens to buyer
        _mint(msg.sender, tokenAmount);

        // Track sales
        totalTokensSold += tokenAmount;
        purchaseHistory[msg.sender] += tokenAmount;

        // Send ETH to treasury
        (bool success, ) = treasuryWallet.call{value: requiredEth}("");
        require(success, "ETH transfer failed");

        // Refund excess ETH
        if (msg.value > requiredEth) {
            (bool refundSuccess, ) = msg.sender.call{
                value: msg.value - requiredEth
            }("");
            require(refundSuccess, "Refund failed");
        }

        emit TokensPurchased(msg.sender, tokenAmount, requiredEth);
    }

    /**
     * @dev Sell tokens to retailers at wholesale price
     * Only owner can authorize retailers and sell to them
     */
    function sellToRetailer(
        address retailer,
        uint256 tokenAmount
    ) external payable onlyOwner nonReentrant {
        if (!retailers[retailer]) revert NotAuthorizedRetailer();

        uint256 wholesalePrice = (tokenPrice * 80) / 100; // 20% discount for retailers
        uint256 requiredEth = (tokenAmount * wholesalePrice) / 10 ** 18;
        if (msg.value < requiredEth) revert InsufficientPayment();

        uint256 newTotalSupply = totalSupply() + tokenAmount;
        if (newTotalSupply > MAX_SUPPLY) revert ExceedsMaxSupply();

        // Mint tokens to retailer
        _mint(retailer, tokenAmount);

        // Track retailer sales
        retailerSales[retailer] += tokenAmount;
        totalTokensSold += tokenAmount;

        emit TokensSoldToRetailer(retailer, tokenAmount, requiredEth);
    }

    /**
     * @dev Override transfer to implement transaction fees
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        if (to == address(0)) revert TransferToZeroAddress();

        // Skip fees for minting/burning or if either party is fee exempt
        if (
            from == address(0) ||
            to == address(0) ||
            feeExempt[from] ||
            feeExempt[to]
        ) {
            super._update(from, to, amount);
            return;
        }

        // Calculate transaction fee
        uint256 feeAmount = (amount * transactionFeeRate) / 10000;
        uint256 transferAmount = amount - feeAmount;

        // Transfer tokens minus fee
        super._update(from, to, transferAmount);

        // Transfer fee to fee collector
        if (feeAmount > 0) {
            super._update(from, feeCollector, feeAmount);
            totalFeesCollected += feeAmount;
            emit TransactionFeeCollected(from, to, feeAmount);
        }
    }

    // ===== ADMIN FUNCTIONS =====

    /**
     * @dev Set transaction fee rate (in basis points)
     */
    function setTransactionFeeRate(uint256 newRate) external onlyOwner {
        if (newRate > MAX_FEE_RATE) revert InvalidFeeRate();
        uint256 oldRate = transactionFeeRate;
        transactionFeeRate = newRate;
        emit FeeRateUpdated(oldRate, newRate);
    }

    /**
     * @dev Set token price for public sales
     */
    function setTokenPrice(uint256 newPrice) external onlyOwner {
        if (newPrice == 0) revert InvalidPrice();
        uint256 oldPrice = tokenPrice;
        tokenPrice = newPrice;
        emit TokenPriceUpdated(oldPrice, newPrice);
    }

    /**
     * @dev Authorize retailer
     */
    function authorizeRetailer(address retailer) external onlyOwner {
        retailers[retailer] = true;
        feeExempt[retailer] = true; // Retailers are fee exempt for business operations
        emit RetailerAuthorized(retailer);
    }

    /**
     * @dev Deauthorize retailer
     */
    function deauthorizeRetailer(address retailer) external onlyOwner {
        retailers[retailer] = false;
        feeExempt[retailer] = false;
        emit RetailerDeauthorized(retailer);
    }

    /**
     * @dev Set fee exemption status
     */
    function setFeeExempt(address account, bool exempt) external onlyOwner {
        feeExempt[account] = exempt;
    }

    /**
     * @dev Update fee collector address
     */
    function setFeeCollector(address newCollector) external onlyOwner {
        require(newCollector != address(0), "Invalid collector address");
        feeCollector = newCollector;
    }

    /**
     * @dev Update treasury wallet
     */
    function setTreasuryWallet(address newTreasury) external onlyOwner {
        require(newTreasury != address(0), "Invalid treasury address");
        treasuryWallet = newTreasury;
    }

    /**
     * @dev Authorize minter
     */
    function authorizeMinter(address minter) external onlyOwner {
        authorizedMinters[minter] = true;
    }

    /**
     * @dev Mint tokens - only for authorized contracts (staking rewards, etc.)
     */
    function mint(address to, uint256 amount) external {
        require(
            authorizedMinters[msg.sender] || msg.sender == owner(),
            "Not authorized to mint"
        );
        uint256 newTotalSupply = totalSupply() + amount;
        if (newTotalSupply > MAX_SUPPLY) revert ExceedsMaxSupply();
        _mint(to, amount);
    }

    // ===== VIEW FUNCTIONS =====

    /**
     * @dev Get purchase cost for token amount
     */
    function getPurchaseCost(
        uint256 tokenAmount
    ) external view returns (uint256) {
        return (tokenAmount * tokenPrice) / 10 ** 18;
    }

    /**
     * @dev Get retailer cost for token amount (wholesale price)
     */
    function getRetailerCost(
        uint256 tokenAmount
    ) external view returns (uint256) {
        uint256 wholesalePrice = (tokenPrice * 80) / 100;
        return (tokenAmount * wholesalePrice) / 10 ** 18;
    }

    /**
     * @dev Calculate transaction fee for amount
     */
    function calculateTransactionFee(
        uint256 amount
    ) external view returns (uint256) {
        return (amount * transactionFeeRate) / 10000;
    }

    /**
     * @dev Get business metrics
     */
    function getBusinessMetrics()
        external
        view
        returns (
            uint256 _totalFeesCollected,
            uint256 _totalTokensSold,
            uint256 _currentPrice,
            uint256 _feeRate,
            uint256 _circulatingSupply
        )
    {
        return (
            totalFeesCollected,
            totalTokensSold,
            tokenPrice,
            transactionFeeRate,
            totalSupply()
        );
    }

    // ===== EMERGENCY FUNCTIONS =====

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency withdraw ETH (only owner)
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        (bool success, ) = owner().call{value: balance}("");
        require(success, "Withdrawal failed");
    }
}

contracts/RevenueSharing.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./ProfitHiveToken.sol";

// Custom errors for gas optimization
error InsufficientRevenue();
error AlreadyDistributed();
error InsufficientStake();
error MinimumStakingPeriod();
error TransferFailed();
error InvalidForecaster();
error InvalidAccuracy();
error RewardNotFound();
error AlreadyClaimed();
error NotAuthorized();
error InvalidAmount();
error InvalidShare();

/**
 * @title RevenueSharing
 * @dev Smart contract for distributing revenue among ProfitHive stakeholders
 * Features:
 * - Automatic revenue distribution based on token holdings
 * - Staking rewards for platform participation
 * - Performance-based rewards for accurate forecasts
 * - Transparent revenue tracking
 */
contract RevenueSharing is Ownable, ReentrancyGuard, Pausable {
    ProfitHiveToken public immutable profitHiveToken;

    struct RevenuePool {
        uint256 totalRevenue;
        uint256 distributedRevenue;
        uint256 timestamp;
        bool isDistributed;
    }

    struct UserStake {
        uint256 amount;
        uint256 stakingTimestamp;
        uint256 lastRewardClaim;
    }

    struct ForecastReward {
        address forecaster;
        uint256 accuracy; // Percentage * 100 (e.g., 9500 = 95%)
        uint256 rewardAmount;
        uint256 timestamp;
        bool claimed;
    }

    // State variables
    mapping(uint256 => RevenuePool) public revenuePools;
    mapping(address => UserStake) public userStakes;
    mapping(address => uint256) public claimableRewards;
    mapping(uint256 => ForecastReward) public forecastRewards;

    uint256 public currentPoolId;
    uint256 public totalStaked;
    uint256 public minStakingPeriod = 7 days;
    uint256 public forecastRewardId;

    // Revenue distribution percentages (basis points, 10000 = 100%)
    uint256 public stakersShare = 6000; // 60%
    uint256 public forecastersShare = 2000; // 20%
    uint256 public platformShare = 2000; // 20%

    // Events
    event RevenueDeposited(uint256 indexed poolId, uint256 amount);
    event RevenueDistributed(uint256 indexed poolId, uint256 totalAmount);
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 amount);
    event ForecastRewardAdded(
        uint256 indexed rewardId,
        address forecaster,
        uint256 accuracy,
        uint256 reward
    );
    event ForecastRewardClaimed(
        uint256 indexed rewardId,
        address forecaster,
        uint256 amount
    );

    constructor(
        address _profitHiveToken,
        address initialOwner
    ) Ownable(initialOwner) {
        profitHiveToken = ProfitHiveToken(_profitHiveToken);
    }

    /**
     * @dev Deposit revenue to be distributed
     */
    function depositRevenue() external payable onlyOwner {
        if (msg.value == 0) revert InsufficientRevenue();

        unchecked {
            ++currentPoolId;
        }
        revenuePools[currentPoolId] = RevenuePool({
            totalRevenue: msg.value,
            distributedRevenue: 0,
            timestamp: block.timestamp,
            isDistributed: false
        });

        emit RevenueDeposited(currentPoolId, msg.value);
    }

    /**
     * @dev Distribute revenue from a specific pool
     */
    function distributeRevenue(uint256 poolId) external onlyOwner nonReentrant {
        RevenuePool storage pool = revenuePools[poolId];
        if (pool.isDistributed) revert AlreadyDistributed();
        if (pool.totalRevenue == 0) revert InsufficientRevenue();

        uint256 stakersAmount;
        uint256 forecastersAmount;
        uint256 platformAmount;

        unchecked {
            stakersAmount = (pool.totalRevenue * stakersShare) / 10000;
            forecastersAmount = (pool.totalRevenue * forecastersShare) / 10000;
            platformAmount = (pool.totalRevenue * platformShare) / 10000;
        }

        // Distribute to stakers proportionally
        if (totalStaked > 0 && stakersAmount > 0) {
            _distributeToStakers(stakersAmount);
        }

        // Keep forecasters and platform amounts in contract for later claims
        pool.distributedRevenue = pool.totalRevenue;
        pool.isDistributed = true;

        emit RevenueDistributed(poolId, pool.totalRevenue);
    }

    /**
     * @dev Stake ProfitHive tokens to earn revenue share
     */
    function stake(uint256 amount) external nonReentrant whenNotPaused {
        if (amount == 0) revert InvalidAmount();

        UserStake storage userStake = userStakes[msg.sender];

        // If user already has a stake, claim pending rewards first
        if (userStake.amount > 0) {
            _claimStakingRewards(msg.sender);
        }

        if (!profitHiveToken.transferFrom(msg.sender, address(this), amount)) {
            revert TransferFailed();
        }

        unchecked {
            userStake.amount += amount;
            totalStaked += amount;
        }

        userStake.stakingTimestamp = block.timestamp;
        userStake.lastRewardClaim = block.timestamp;

        emit Staked(msg.sender, amount);
    }

    /**
     * @dev Unstake tokens
     */
    function unstake(uint256 amount) external nonReentrant {
        UserStake storage userStake = userStakes[msg.sender];
        if (userStake.amount < amount) revert InsufficientStake();
        if (block.timestamp < userStake.stakingTimestamp + minStakingPeriod) {
            revert MinimumStakingPeriod();
        }

        // Claim pending rewards first
        _claimStakingRewards(msg.sender);

        unchecked {
            userStake.amount -= amount;
            totalStaked -= amount;
        }

        if (!profitHiveToken.transfer(msg.sender, amount)) {
            revert TransferFailed();
        }

        emit Unstaked(msg.sender, amount);
    }

    /**
     * @dev Claim staking rewards
     */
    function claimStakingRewards() external nonReentrant {
        _claimStakingRewards(msg.sender);
    }

    /**
     * @dev Add forecast accuracy reward
     */
    function addForecastReward(
        address forecaster,
        uint256 accuracy,
        uint256 rewardAmount
    ) external onlyOwner {
        if (forecaster == address(0)) revert InvalidForecaster();
        if (accuracy > 10000) revert InvalidAccuracy();

        unchecked {
            ++forecastRewardId;
        }

        forecastRewards[forecastRewardId] = ForecastReward({
            forecaster: forecaster,
            accuracy: accuracy,
            rewardAmount: rewardAmount,
            timestamp: block.timestamp,
            claimed: false
        });

        emit ForecastRewardAdded(
            forecastRewardId,
            forecaster,
            accuracy,
            rewardAmount
        );
    }

    /**
     * @dev Claim forecast accuracy reward
     */
    function claimForecastReward(uint256 rewardId) external nonReentrant {
        ForecastReward storage reward = forecastRewards[rewardId];
        if (reward.forecaster != msg.sender) revert NotAuthorized();
        if (reward.claimed) revert AlreadyClaimed();

        reward.claimed = true;

        // Send ETH reward
        (bool success, ) = payable(msg.sender).call{value: reward.rewardAmount}(
            ""
        );
        if (!success) revert TransferFailed();

        emit ForecastRewardClaimed(rewardId, msg.sender, reward.rewardAmount);
    }

    /**
     * @dev Internal function to distribute rewards to stakers
     */
    function _distributeToStakers(uint256 totalAmount) internal {
        // This adds to claimable rewards proportionally
        // Implementation simplified for demo - in production, would use a more gas-efficient mechanism
    }

    /**
     * @dev Internal function to claim staking rewards
     */
    function _claimStakingRewards(address user) internal {
        uint256 rewards = claimableRewards[user];
        if (rewards > 0) {
            claimableRewards[user] = 0;
            (bool success, ) = payable(user).call{value: rewards}("");
            if (!success) revert TransferFailed();
            emit RewardsClaimed(user, rewards);
        }
    }

    /**
     * @dev Get user's staking information
     */
    function getUserStakeInfo(
        address user
    )
        external
        view
        returns (
            uint256 stakedAmount,
            uint256 stakingTimestamp,
            uint256 claimableAmount,
            bool canUnstake
        )
    {
        UserStake memory userStake = userStakes[user];
        return (
            userStake.amount,
            userStake.stakingTimestamp,
            claimableRewards[user],
            block.timestamp >= userStake.stakingTimestamp + minStakingPeriod
        );
    }

    /**
     * @dev Emergency withdrawal (only owner)
     */
    function emergencyWithdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    /**
     * @dev Update revenue sharing percentages
     */
    function updateRevenueShares(
        uint256 _stakersShare,
        uint256 _forecastersShare,
        uint256 _platformShare
    ) external onlyOwner {
        unchecked {
            if (_stakersShare + _forecastersShare + _platformShare != 10000) {
                revert InvalidShare();
            }
        }

        stakersShare = _stakersShare;
        forecastersShare = _forecastersShare;
        platformShare = _platformShare;
    }

    /**
     * @dev Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

